mapper.update:
update ap_article
set static_url = #{article.staticUrl}
where id = #{article.id}

mapper.select:
select * from ap_article aa
left join ap_article_config aac on aa.id = aac.article_id

    aac.is_delete != 1
    and aac.is_down != 1

        and aa.publish_time <= #{articleHomeDto.minBehotTime}


        and aa.publish_time >= #{articleHomeDto.maxBehotTime}


        and aa.channel_id = #{articleHomeDto.tag}


order by aa.publish_time desc
limit #{articleHomeDto.size}
